import React from 'react'
import '../css/signup.css'

export default function SignUp({ isActive }) {

    const [formData, setFormData] = React.useState({
        username : '',
        email: '',
        password: '',
        password2: '',
    })

    function handleChange(e) {

        const inputName = e.target.name;
        const inputValue = e.target.value

        setFormData(prevFormData => ({
            ...prevFormData,
            [inputName] : inputValue
        }))
    }   

    function handleSubmit(e) {
        e.preventDefault()
        fetch('/api/auth/register', {
            method: 'POST',
            body: JSON.stringify(formData)
        })
            .then(res => res.json())
            .then(data => console.log(data))
    }

    return (
        // Input for username, password, email
        <div className={`modal-bg ${isActive ? 'bg-active' : ''}`}>
            <form className='form'>
                <h2>Sign Up</h2>
                <input name='username' value={formData.username} onChange={handleChange} type='text' placeholder='Enter your username'></input>
                <input name='email' value={formData.email} onChange={handleChange} type='email' placeholder='Enter your email'></input>
                <input name='password' value={formData.password} onChange={handleChange} type='text' placeholder='Enter your password'></input>
                <input name='password2' value={formData.password2} onChange={handleChange} type='text' placeholder='Confirm Password'></input>
                <button onClick={handleSubmit}>Submit</button>
            </form>
        </div>
    )
}
