import React from 'react'
import '../css/App.css';
import { Link } from "react-router-dom";
import Logo from '../assets/default-monochrome-black.svg'
import UserAuth from './UserAuth'

// <button><Link to='/mainfeed'>dick</Link></button>

function App() {

  const [isActive, setIsActive] = React.useState(false)
  const [chosenAuth, setChosenAuth] = React.useState('')

  function handleClick(e) {
    
    toggleModal()

    chooseAuth(e.target.name)
  }

  function toggleModal() {
    setIsActive(prevState => !prevState)
  }

  function chooseAuth(name) {
    setChosenAuth(name)
  }

  function closeModal() {
    toggleModal()
  }

  return (
    <div className="app">
      <div className='logo-container'>
        <img src={Logo}/>
      </div>
      <div className='link-container'>
        <button><Link className='link' to='/social'>Social</Link></button>
        <button><Link className='link' to='/fitness'>Fitness</Link></button>
      </div>
      <div className='btn-container'>
        <button className='auth-btn' name='signUp' onClick={handleClick}>Sign Up</button>
        <button className='auth-btn' name='signIn' onClick={handleClick}>Sign In</button>
      </div>
      <UserAuth isActive={isActive} chosenAuth={chosenAuth} closeModal={closeModal}/>
    </div>
  );
}

export default App;
