import React from 'react'

function MenuOption({ name, displayOption, toggleMenu }) {

    function onClick(e) {
        displayOption(e)
        toggleMenu()
    }

    return (
      <li onClick={onClick}>{name}</li>
    )
}

export default MenuOption
