import React from 'react'
import '../css/userAuth.css'
import CloseIcon from '@mui/icons-material/Close';

export default function SignUp({ isActive, chosenAuth, closeModal }) {

    const initialFormValue = {
        username : '',
        email: '',
        password: '',
        password2: '',
    }

    const [registerFormData, setRegisterFormData] = React.useState(initialFormValue)
    const [loginFormData, setLoginFormData] = React.useState(initialFormValue)

    const [userInfo, setUserInfo] = React.useState({})

    function handleChange(e) {

        const inputName = e.target.name;
        const inputValue = e.target.value

        setRegisterFormData(prevRegisterFormData => ({
            ...prevRegisterFormData,
            [inputName] : inputValue
        }))
    }   

    function registerNewUser(e) {
        e.preventDefault()
        fetch('/api/auth/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(registerFormData)
        })
            .then(res => res.json())
            .then(data => console.log(data))

        setRegisterFormData(initialFormValue)
    }

    function loginUser() {
        fetch('/api/auth/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(registerFormData)
        })
            .then(res => res.json())
            .then(data => console.log(data))
    }

    return (
        // Input for username, password, email
        <div className={`modal-bg ${isActive ? 'bg-active' : ''}`}>
            
            {chosenAuth === 'signIn'

                ? <form className='form'>
                    <CloseIcon className='close-icon' onClick={closeModal}/>
                    <h2>Sign In</h2>
                    <input name='username' value={registerFormData.username} onChange={handleChange} type='text' placeholder='Enter your username'></input>
                    <input name='password' value={registerFormData.password} onChange={handleChange} type='password' placeholder='Enter your password'></input>
                    <button onClick={loginUser}>Submit</button>
                </form>
            
                : <form className='form'>
                    <CloseIcon className='close-icon' onClick={closeModal}/>
                    <h2>Sign Up</h2>
                    <input name='username' value={registerFormData.username} onChange={handleChange} type='text' placeholder='Enter a username'></input>
                    <input name='email' value={registerFormData.email} onChange={handleChange} type='email' placeholder='Enter an email'></input>
                    <input name='password' value={registerFormData.password} onChange={handleChange} type='password' placeholder='Enter a password'></input>
                    <input name='password2' value={registerFormData.password2} onChange={handleChange} type='password' placeholder='Confirm Password'></input>
                    <button onClick={registerNewUser}>Submit</button>
                </form>
            }
        </div>
    )
}
