import React from 'react'
import '../css/signup.css'

export default function SignUp({ isActive }) {

    const [formData, setFormData] = React.useState({
        username : '',
        email: '',
        password: '',
        password2: '',
    })

    function handleChange(e) {

        const inputName = e.target.name;
        const inputValue = e.target.value

        if (inputName === 'username') {
            setUsername(inputValue)
        } else if (inputName === 'password') {
            setPassword(inputValue)
        } else if (inputName === 'email') {
            setEmail(inputValue)
        }
    }   

    return (
        // Input for username, password, email
        <div className={`modal-bg ${isActive ? 'bg-active' : ''}`}>
            <form className='form'>
                <h2>Sign Up</h2>
                <input name='username' value={username} onChange={handleChange} type='text' placeholder='Enter your username'></input>
                <input name='password' value={password} onChange={handleChange} type='text' placeholder='Enter your password'></input>
                <input name='password2' value={password2} onChange={handleChange} type='text' placeholder='Confirm Password'></input>
                <input name='email' value={email} onChange={handleChange} type='email' placeholder='Enter your email'></input>
                <button >Submit</button>
            </form>
        </div>
    )
}
