import React, { useState } from "react";

export default function NewExercise({ exerciseName, setCount, repRange }) {

    const initialExerciseInfo = {
        workoutName: '',
        setCount: '',
        repRange: 0
    }

    const [exerciseInfo, setExerciseInfo] = useState(initialExerciseInfo)

    function onChange(e) {
        setExerciseInfo(prevInfo => ({
            ...prevInfo,
            [e.target.name] : e.target.value
        }))
    }

    return (
        <tr>
            <td>
                <input value={exerciseInfo.workoutName} onChange={onChange} name='workoutName' placeholder='Bench Press'></input>
            </td>
            <td>
                <input value={exerciseInfo.setCount} onChange={onChange} name='setCount' placeholder='3'></input>
            </td>
            <td >
                <input value={exerciseInfo.repRange} onChange={onChange} name='repRange' placeholder='8-12'></input>
            </td>
        </tr>
    )
}
