import React, { useState } from "react";
import NewExercise from "./NewExercise";
import AddIcon from '@mui/icons-material/Add';

export default function AddWorkout() {

    const [key, setKey] = useState(1)
    const [exerciseList, setExerciseList] = useState([<NewExercise key={0}/>])

    function addExercise() {
        setKey(key => key + 1)
        setExerciseList([...exerciseList, <NewExercise key={key}/>])
    }

    function submitWorkout() {
        fetch('/api/workouts/add-workout', {
            method: 'POST'
        })
            .then(res => res.json())
            .then(data => console.log(data))
    }

    return (
        <>
            <input placeholder='Enter workout name'></input>
            <table className='exercise-table'>
                <tbody>
                    <tr>
                        <th>Exercise</th>
                        <th># of Sets</th>
                        <th>Rep Range</th>
                    </tr>
                    {exerciseList}
                </tbody>
            </table>
            <AddIcon onClick={addExercise} />
            <button onClick={submitWorkout}>Submit Workout</button>
        </>
    )
}
