import React from 'react'
import '../css/App.css';
import { Link } from "react-router-dom";
import Logo from '../assets/default-monochrome-black.svg'
import UserAuth from './UserAuth'

// <button><Link to='/mainfeed'>dick</Link></button>

function App() {

  const [isActive, setIsActive] = React.useState(false)
  const [chosenAuth, setChosenAuth] = React.useState('')

  function handleClick(e) {
    
    toggleModal()

    chooseAuth(e.target.name)
  }

  function toggleModal() {
    setIsActive(prevState => !prevState)
  }

  function chooseAuth(name) {
    setChosenAuth(name)
  }

  function closeModal() {
    toggleModal()
  }

  // ***** USER AUTH *****

  const initialFormValue = {
    username : '',
    email: '',
    password: '',
    password2: '',
}

  const [formData, setFormData] = React.useState(initialFormValue)

  const [userInfo, setUserInfo] = React.useState({})

  function handleChange(e) {

      const inputName = e.target.name;
      const inputValue = e.target.value

      setFormData(prevFormData => ({
          ...prevFormData,
          [inputName] : inputValue
      }))
  }   

  function registerNewUser(e) {
      e.preventDefault()
      fetch('/api/auth/register', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
      })
          .then(res => res.json())
          .then(data => console.log(data))

      setFormData(initialFormValue)
  }

  function loginUser(e) {
      e.preventDefault()
      fetch('/api/auth/login', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
      })
          .then(res => res.json())
          .then(data => setUserInfo(data))

      console.log(userInfo)
      setFormData(initialFormValue)
  }

  return (
    <div className="app">
      <div className='logo-container'>
        <img src={Logo} alt='logo'/>
      </div>
      <div className='link-container'>
        <button><Link className='link' to='/social'>Social</Link></button>
        <button><Link className='link' to='/fitness'>Fitness</Link></button>
      </div>
      <div className='btn-container'>
        <button className='auth-btn' name='signUp' onClick={handleClick}>Sign Up</button>
        <button className='auth-btn' name='signIn' onClick={handleClick}>Sign In</button>
      </div>
      <UserAuth
        isActive={isActive}
        chosenAuth={chosenAuth}
        closeModal={closeModal}
        loginUser={loginUser}
        registerNewUser={registerNewUser}
        handleChange={handleChange}
      />
    </div>
  );
}

export default App;
