import React, { useEffect, useState, useRef } from 'react'
import '../css/logger.css'
import AddWorkout from './AddWorkout'
import Exercise from './Exercise'
import Navbar from './Navbar'
import FriendList from './FriendList'
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import MenuOption from './MenuOption'

export default function Logger() {

    const [addWorkout, setAddWorkout] = useState(false)
    const [toggleDropdown, setToggleDropdown] = useState(false)
    const displayedOption = useRef()

    useEffect(() => {
        console.log(displayedOption)
    }, [])
    

    function displayOption(e) {
        console.log(e.target.innerText)
        // displayedOption.current.innerText = 0
    }

    return (
        // Have an example, starter workout
        // Everyone is part of it at first
        // Called 'IRON Workout'

        <div className='main'>
            <Navbar />
            <div className='body'>
                <FriendList />
                <div className='container'>
                    {addWorkout ? (<AddWorkout />) : 
        
                    (<div className='default'>
                        <div className='dropdown'>
                            <div className={`select ${toggleDropdown && 'select-clicked'}`}>
                                <h1 className='selected' ref={displayedOption}>Wendler's 5/3/1</h1>
                                <ArrowDropDownIcon className={`caret ${toggleDropdown && 'caret-rotate'}`} style={{fontSize: 40}} onClick={() => setToggleDropdown(prevState => !prevState)}/>
                            </div>
                            <ul className={`dropdown-menu ${toggleDropdown && 'menu-open'}`}>
                                {/* <MenuOptions className='menu-option' name='NSuns' onClick={() => setToggleDropdown(prevState => !prevState)}/> */}
                                <MenuOption name='Michael' displayOption={displayOption}/>
                                <li className='menu-option' name='NSuns' onClick={() => setToggleDropdown(prevState => !prevState)}>Add workout</li>
                                <li>Michael's Workout</li>
                                <li>Johns's Workout</li>
                                <li className='active'>Wendler's 5/3/1</li>
                            </ul>
                        </div>
                        <button onClick={() => setAddWorkout(true)}>Add workout</button>
                        <table className='exercise-table'>
                            <tbody>
                                <tr>
                                    <th>Exercise</th>
                                    <th>Weight</th>
                                    <th>Reps</th>
                                </tr>
                                <Exercise />
                                <Exercise />
                                <Exercise />
                                <Exercise />
                                <Exercise />
                                <Exercise />
                                <Exercise />
                            </tbody>
                        </table>
                    </div>)}
                </div>
            </div>
        </div>

        
        // <div className='logger'>
        //     <h1>Wendler's 5/3/1</h1>
        //     <div className='grid'>
        //         <Set />
        //         <Set />
        //         <Set />
        //         <Set />
        //         <Set />
        //         <Set />
        //     </div>
        // </div>
    )
}
