import { Avatar } from '@mui/material'
import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'
import '../css/userAvatar.css'

function UserAvatar({ profilePic, name, styling }) {
  
    // first word of styling goes to div
    // second word goes to avatar component
    let stylingArray

    if (styling) {
        stylingArray = styling.split(' ')
    }
    

    return (
        <div className={styling && stylingArray[0]}>
            <h3>{name}</h3>
            <Avatar className={styling && stylingArray[1]} src={profilePic ? profilePic.url : ''} />
        </div>
    )
}

export default UserAvatar
