import React, { useState } from "react";
import NewExercise from "./NewExercise";
import AddIcon from '@mui/icons-material/Add';
import { useSelector } from "react-redux";


export default function AddWorkout() {

    const [keyId, setKeyId] = useState(0)
    const [workoutName, setWorkoutName] = useState('')
    const [exerciseList, setExerciseList] = useState([])

    // gives workout name and info
    const workoutInfo = useSelector(state => state.workout)
    
    function addExercise() {
        setKeyId(prevKey => prevKey + 1)
        setExerciseList(prevList => [
            ...prevList,
            <NewExercise />
        ])
        setExerciseInfo(prevInfo => [
            ...prevInfo,
            {
                ...initialExerciseInfo,
                key: keyId
            }
        ])
    }

    function submitWorkout() {
        fetch('/api/workouts/add-workout', {
            method: 'POST'
        })
            .then(res => res.json())
            .then(data => console.log(data))
    }

    return (
        <>
            <input placeholder='Enter workout name' value={workoutName} ></input>
            <table className='exercise-table'>
                <tbody>
                    <tr>
                        <th>Exercise</th>
                        <th># of Sets</th>
                        <th>Rep Range</th>
                        <th>Lock</th>
                    </tr>
                    {/* <NewExercise key={keyId} id={keyId} exerciseInfo={exerciseInfo} handleExerciseInfo={handleExerciseInfo}/> */}
                    {exerciseList}
                </tbody>
            </table>
            <AddIcon onClick={addExercise} />
            <button onClick={submitWorkout}>Submit Workout</button>
        </>
    )
}
