import React from 'react'

export default function SignUp() {

    const [username, setUsername] = React.useState('')
    const [password, setPassword] = React.useState('')
    const [email, setEmail] = React.useState('')

    const [putUsername, setPutUsername] = React.useState('');
    const [putPassword, setPutPassword] = React.useState('');

    function handleChange(e) {

        const inputName = e.target.name;
        const inputValue = e.target.value

        if (inputName === 'username') {
            setUsername(inputValue)
        } else if (inputName === 'password') {
            setPassword(inputValue)
        } else if (inputName === 'email') {
            setEmail(inputValue)
        } else if (inputName === 'putUsername') {
            setPutUsername(inputValue)
        } else if (inputName === 'putPassword') {
            setPutPassword(inputValue)
        }
    }   


    function handleClick(e) {
        e.preventDefault();
        fetch('/api/user', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                'username' : username,
                'password' : password,
                'email' : email,
            })
        });

        setUsername('')
        setPassword('')
        setEmail('')
    }

    return (
        // Input for username, password, email
        <div>
            <form>
                <input name='username' value={username} onChange={handleChange} type='text' placeholder='username'></input>
                <input name='password' value={password} onChange={handleChange} type='text' placeholder='password'></input>
                <input name='email' value={email} onChange={handleChange} type='email' placeholder='email'></input>
                <button onClick={handleClick}>Submit</button>
            </form>

            <form>
                <input name='putUsername' value={putUsername} onChange={handleChange} type='text' placeholder='Enter existing username'></input>
                <input name='putPassword' value={putPassword} onChange={handleChange} type='text' placeholder='Enter existing password'></input>
                <button onClick={validateUserInfo}>Submit</button>
            </form>
        </div>
    )
}
