import React, { useState } from 'react'
import Exercise from './Exercise'
import '../css/workoutTracker.css'

function WorkoutTracker({ workout }) {

    console.log(workout)

    const { exercises } = workout

    function handleChange(e, index) {
        console.log(e.target.value)
    }

    // Exercises is an array of exercise objects
    const exerciseList = exercises.map((exercise, index) => {
        return <Exercise key={index} exercise={exercise} onChange={e => handleChange(e, index)}/>
    })

    function logWorkout() {
        console.log('hello')
    }

    return (
        <>
            <table className='exercise-table'>
                <tbody>
                    <tr>
                        <th>Exercise</th>
                        <th>Weight</th>
                        <th>Reps</th>
                    </tr>
                    {exerciseList}
                </tbody>
            </table>
            <button onClick={logWorkout}>Finished Workout</button>
        </>
    )
}

export default WorkoutTracker
