import React from 'react'
import '../css/userAuth.css'
import CloseIcon from '@mui/icons-material/Close';

export default function SignUp({
    isActive,
    chosenAuth,
    closeModal,
    loginUser,
    registerNewUser,
    handleChange,
    formData,
    setFormData,
    userInfo,
    setUserInfo,
    initialFormValue
}) {

