import React, { useState } from "react";
import NewExercise from "./NewExercise";
import AddIcon from '@mui/icons-material/Add';

export default function AddWorkout() {

    const initialExerciseInfo = {
        key: null,
        workoutName: '',
        setCount: '',
        repRange: 0
    }

    const [key, setKey] = useState(1)
    const [workoutName, setWorkoutName] = useState('')
    const [exerciseList, setExerciseList] = useState([])
    const [exerciseInfo, setExerciseInfo] = useState([])

    function handleExerciseInfo(e) {
        setExerciseInfo(prevInfo => ({
            ...prevInfo,
            [e.target.name] : e.target.value
        }))
    }
    
    function addExercise() {
        setKey(key => key + 1)
        setExerciseList(prevList => [
            ...prevList,
            <NewExercise key={key} />
        ])
    }

    function handleName(e) {
        setWorkoutName(e.target.value)
    }

    function submitWorkout() {
        fetch('/api/workouts/add-workout', {
            method: 'POST'
        })
            .then(res => res.json())
            .then(data => console.log(data))
    }

    return (
        <>
            <input placeholder='Enter workout name' value={workoutName} onChange={handleName}></input>
            <table className='exercise-table'>
                <tbody>
                    <tr>
                        <th>Exercise</th>
                        <th># of Sets</th>
                        <th>Rep Range</th>
                    </tr>
                    <NewExercise key={key} id={key}/>
                    <NewExercise key={key} id={key}/>
                </tbody>
            </table>
            <AddIcon onClick={addExercise} />
            <button onClick={submitWorkout}>Submit Workout</button>
        </>
    )
}
