import React from 'react'
import { Avatar, IconButton } from '@mui/material'
import '../css/makepost.css'

export default function MakePost() {

    const [postInfo, setPostInfo] = React.useState('')

    React.useEffect(() => {
        fetch('/api/posts')
            .then(res => res.json())
            .then(data => console.log(data))
    }, [])

    function handleInput(e) {
        setPostInfo(e.target.value)
    }

    function handlePost(e) {

        const postOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({content : postInfo})
        }

        fetch('/api/posts', postOptions)
            .then(res => res.json())
            .then(data => console.log(data))

        setPostInfo('')
    }

    return (
        <div className='make__post'>
            <div className='make__post--top'>
                <Avatar src='' />
                <input onChange={handleInput} value={postInfo} className='text-input' placeholder='Enter text: ' />
                <input placeholder='Add image' />
            </div>
            <div className='make__post--bottom'>
                <IconButton onClick={handlePost}>Post</IconButton>
            </div>
        </div>
    )
}
